CREATE TABLE IF NOT EXISTS restaurants (
    id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
    namaRestaurant VARCHAR(100) NOT NULL,
    kategoriRestaurant_id UUID REFERENCES kategori_restaurant(id),
    lokasi VARCHAR(100) NOT NULL CHECK (lokasi IN (
        'Blok-M Square',
        'Plaza Blok-M',
        'Melawai',
        'Taman Literasi',
        'Barito',
        'Gulai Tikungan (Mahakam)',
        'Senayan',
        'Kebayoran Baru'
    )),
    rating DECIMAL(3,2) CHECK (rating >= 0 AND rating <= 5),  
    price NUMRANGE NOT NULL,
    informasiRestaurant TEXT NOT NULL,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE spots (
    id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
    namaTempat VARCHAR(100) NOT NULL,
    kategoriSpot_id UUID REFERENCES kategori_spot(id),
    lokasi VARCHAR(100) NOT NULL CHECK (lokasi IN (
        'Blok-M Square',
        'Plaza Blok-M',
        'Melawai',
        'Taman Literasi',
        'Barito',
        'Gulai Tikungan (Mahakam)',
        'Senayan',
        'Kebayoran Baru'
    )),
    rating DECIMAL(3,2) CHECK (rating >= 0 AND rating <= 5),
    price NUMRANGE NOT NULL,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

--- Tabel Catalog
CREATE TABLE IF NOT EXISTS catalogs (
    id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
    namaCatalog VARCHAR(100) NOT NULL,
    kategoriRestaurant_id UUID REFERENCES kategori_restaurant(id),  
    lokasi VARCHAR(100) NOT NULL CHECK (lokasi IN (
        'Blok-M Square',
        'Plaza Blok-M',
        'Melawai',
        'Taman Literasi',
        'Barito',
        'Gulai Tikungan (Mahakam)',
        'Senayan',
        'Kebayoran Baru'
    )),
    /*rating DECIMAL(3,2) CHECK (rating >= 0 AND rating <= 5),*/
    harga INTEGER NOT NULL,
    deskripsiCatalog TEXT NOT NULL,
    restaurant_id UUID REFERENCES restaurant(id) ON DELETE CASCADE, 
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

--- Artikel Tabel
CREATE TABLE IF NOT EXISTS articles (
    id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
    judulArtikel VARCHAR(255) NOT NULL,
    kontenArtikel TEXT NOT NULL,
    image_url VARCHAR(255),
    restaurant_id UUID REFERENCES restaurants(id) ON DELETE SET NULL,
    spot_id UUID REFERENCES spots(id) ON DELETE SET NULL,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

